#!/bin/bash

B=`basename $0 .sh`
cd `dirname $0`
source ./rungraphd

# Walk tentacle ends of a squid. 

rm -rf $D
rungraphd -d${D} -bty <<-'EOF'
	write (value="1" name="a")
	write (value="1" name="b")
	write (value="1" name="c")
	write (value="2" name="chaff")
	read (GUID=(00000012400034568000000000000000
		    00000012400034568000000000000001
		    00000012400034568000000000000002)
		result=(cursor (name)) pagesize=1)
	read (GUID=(00000012400034568000000000000000
		    00000012400034568000000000000001
		    00000012400034568000000000000002)
		cursor="cursor:d210:fixed:3:0,1,2/1/"
		result=(cursor (name)) pagesize=1)
	read (GUID=(00000012400034568000000000000000
		    00000012400034568000000000000001
		    00000012400034568000000000000002)
		cursor="cursor:d21c:fixed:3:0,1,2/2/"
		result=(cursor (name)) pagesize=1)
	read (GUID=(00000012400034568000000000000000
		    00000012400034568000000000000001
		    00000012400034568000000000000002)
	      cursor="cursor:7931:fixed:1:2")
	read id="error:changed-the-checksum" (GUID=(00000012400034568000000000000000
		    00000012400034568000000000000001
		    00000012400034568000000000000002)
	      cursor="cursor:d219:fixed:3:0,1,2/3/")
	read id="error:changed-the-iterator" (GUID=(00000012400034568000000000000000
		    00000012400034568000000000000001
		    00000012400034568000000000000002)
	      cursor="cursor:d218:fixed:3:0,1,3/3/")
	read id="error:changed-the-expression" (GUID=(00000012400034568000000000000000
		    00000012400034568000000000000002)
	      cursor="cursor:d218:fixed:3:0,1,2/3/")
	read (GUID=(00000012400034568000000000000000
		    00000012400034568000000000000001
		    00000012400034568000000000000002)
		cursor="cursor:d218:fixed:3:0,1,2/3/"
		cursor="cursor:d218:fixed:3:0,1,2/3/"
		result=(cursor (name)) pagesize=1)
	read (GUID=(00000012400034568000000000000000
		    00000012400034568000000000000001
		    00000012400034568000000000000002)
		cursor="i am not a cursor!!!"
		result=(cursor (name)) pagesize=1)
	read (GUID=(00000012400034568000000000000000
		    00000012400034568000000000000001
		    00000012400034568000000000000002)
		cursor=""
		result=(cursor (name)) pagesize=1)
EOF
rm -rf $D
